import React, { useState } from "react";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

function MeetingMode() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Estado para la barra lateral
  const [isRecording, setIsRecording] = useState(false); // Estado para grabación
  const [response, setResponse] = useState("Aquí aparecerán los resultados."); // Estado para mostrar el resultado

  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev);
  };

  const startRecording = () => {
    setIsRecording(true);
    setResponse("Grabación en curso...");
    // Lógica para iniciar la grabación
  };

  const stopRecording = () => {
    setIsRecording(false);
    setResponse("Grabación detenida y enviada.");
    // Lógica para detener y enviar la grabación
  };

  const cancelRecording = () => {
    setIsRecording(false);
    setResponse("Grabación cancelada.");
    // Lógica para cancelar la grabación
  };

  return (
    <div className="bg-white min-h-screen flex flex-col">
      {/* Botón de hamburguesa */}
      <button
        className="fixed top-4 left-4 text-2xl text-white bg-blue-800 p-2 rounded-md shadow-md hover:bg-blue-600 transition duration-300 z-50"
        onClick={toggleSidebar}
      >
        ☰
      </button>

      {/* Header */}
      <Header />
      {/* Sidebar */}
      <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />

      {/* Contenido principal */}
      <main className="flex-1 flex flex-col items-center justify-center p-8 lg:ml-64 relative">
        <h1 className="text-3xl font-bold text-blue-800 mb-6 flex items-center gap-3">
          <i className="fas fa-microphone text-blue-500"></i>
          Modo Reunión
        </h1>
        <div className="bg-gradient-to-br from-blue-600 to-blue-800 text-white p-8 rounded-lg shadow-lg w-full max-w-lg relative">
          {/* Icono animado en el cuadro */}
          <div className="absolute -top-8 left-1/2 transform -translate-x-1/2 bg-red-500 w-16 h-16 rounded-full flex items-center justify-center shadow-lg animate-pulse">
            <i className="fas fa-record-vinyl text-3xl text-white"></i>
          </div>
          {/* Botones */}
          <div className="flex flex-col gap-4 mt-10">
            <button
              className={`py-3 px-4 rounded-md font-semibold flex items-center justify-center gap-2 transition ${
                isRecording
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-white text-blue-800 hover:bg-gray-100"
              }`}
              onClick={startRecording}
              disabled={isRecording}
            >
              <i className="fas fa-play text-blue-800"></i>
              Iniciar Grabación
            </button>
            <button
              className={`py-3 px-4 rounded-md font-semibold flex items-center justify-center gap-2 transition ${
                isRecording
                  ? "bg-red-600 text-white hover:bg-red-700"
                  : "bg-gray-400 cursor-not-allowed"
              }`}
              onClick={stopRecording}
              disabled={!isRecording}
            >
              <i className="fas fa-stop text-white"></i>
              Detener y Enviar
            </button>
            <button
              className={`py-3 px-4 rounded-md font-semibold flex items-center justify-center gap-2 transition border ${
                isRecording
                  ? "border-red-600 text-red-600 hover:bg-red-600 hover:text-white"
                  : "border-gray-400 text-gray-400 cursor-not-allowed"
              }`}
              onClick={cancelRecording}
              disabled={!isRecording}
            >
              <i className="fas fa-times text-red-600"></i>
              Cancelar
            </button>
          </div>
          {/* Resultado */}
          <div className="mt-6 bg-gray-100 text-blue-800 p-4 rounded-md text-center">
            {response}
          </div>
        </div>
      </main>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default MeetingMode;
