import React, { useState } from "react";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

function MainMenu() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Estado para controlar la visibilidad de la barra lateral

  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev); // Cambia entre abierto/cerrado
  };

  return (
    <div className="bg-white min-h-screen flex flex-col">
      {/* Botón de hamburguesa fuera del Header */}
      <button
        className="absolute top-4 left-4 text-2xl text-white bg-blue-800 p-2 rounded-md shadow-md hover:bg-blue-600 transition duration-300 z-50"
        onClick={toggleSidebar}
      >
        ☰
      </button>

      {/* Header */}
      <Header />
      {/* Sidebar */}
      <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />
      <main className={`flex-1 flex items-center justify-center ${isSidebarOpen ? "lg:ml-64" : ""}`}>
        {/* Cuadro azul con el botón */}
        <div className="fixed bg-gradient-to-br from-blue-600 to-blue-800 p-6 rounded-lg shadow-lg text-white flex flex-col items-center">
          <h2 className="text-2xl font-bold mb-4">Menú Principal</h2>
          <button
            className="bg-white text-blue-800 py-3 px-6 rounded-lg font-semibold flex items-center gap-3 hover:bg-gray-100 hover:scale-105 transition-transform duration-200"
            onClick={() => alert("Iniciar reunión")}
          >
            <i className="fas fa-video text-xl"></i>
            Iniciar Reunión
          </button>
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default MainMenu;
