import React, { useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

function MeetingMode() {
  const [isRecording, setIsRecording] = useState(false); // Estado para grabación
  const [response, setResponse] = useState("Aquí aparecerán los resultados."); // Estado para mostrar el resultado

  const startRecording = () => {
    setIsRecording(true);
    setResponse("Grabación en curso...");
    // Lógica para iniciar la grabación (simulada)
  };

  const stopRecording = () => {
    setIsRecording(false);
    setResponse("Grabación detenida y enviada.");
    // Lógica para detener y enviar la grabación (simulada)
  };

  const cancelRecording = () => {
    setIsRecording(false);
    setResponse("Grabación cancelada.");
    // Lógica para cancelar la grabación (simulada)
  };

  return (
    <div className="bg-[#221111] min-h-screen flex flex-col items-center justify-center text-white font-sans">
      {/* Encabezado */}
      <Header />
      <header className="text-center text-3xl font-bold mb-6">Modo Reunión</header>

      {/* Contenido principal */}
      <main className="bg-[#1E1E1E] rounded-lg p-8 shadow-lg max-w-lg w-full">
        <section className="flex flex-col gap-4">
          {/* Botón para iniciar grabación */}
          <button
            className={`py-2 px-4 rounded-md font-bold transition-colors ${
              isRecording ? "bg-gray-500 cursor-not-allowed" : "bg-blue-500 hover:bg-blue-600"
            }`}
            onClick={startRecording}
            disabled={isRecording}
          >
            Iniciar Grabación
          </button>

          {/* Botón para detener grabación */}
          <button
            className={`py-2 px-4 rounded-md font-bold transition-colors ${
              isRecording ? "bg-red-500 hover:bg-red-600" : "bg-gray-500 cursor-not-allowed"
            }`}
            onClick={stopRecording}
            disabled={!isRecording}
          >
            Detener y Enviar
          </button>

          {/* Botón para cancelar grabación */}
          <button
            className={`py-2 px-4 rounded-md font-bold transition-colors border ${
              isRecording ? "border-red-500 text-red-500 hover:bg-red-500 hover:text-white" : "border-gray-500 text-gray-500 cursor-not-allowed"
            }`}
            onClick={cancelRecording}
            disabled={!isRecording}
          >
            Cancelar
          </button>
        </section>

        {/* Área de resultados */}
        <div className="mt-6 bg-[#2C2C2C] rounded-md p-4 text-center font-mono">
          {response}
        </div>
      </main>

      {/* Pie de página */}
      <footer className="mt-6 text-center text-sm text-gray-400">
        <p>&copy; 2024 Alphaomega. Todos los derechos reservados.</p>
        <a
          href="/menu"
          className="flex items-center justify-center gap-2 text-blue-400 hover:underline mt-4"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-5 w-5"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            strokeWidth="2"
          >
            <path strokeLinecap="round" strokeLinejoin="round" d="M15 19l-7-7 7-7" />
          </svg>
          Volver al Menú Principal
        </a>
      </footer>
    </div>
  );
}

export default MeetingMode;
