import React, { useState } from "react";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

function TextSummarizer() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Estado para la barra lateral
  const [uploadedText, setUploadedText] = useState(""); // Texto cargado del archivo
  const [summary, setSummary] = useState(""); // Resultado del resumen
  const [processing, setProcessing] = useState(false); // Estado de procesamiento

  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUploadedText(e.target.result);
      };
      reader.readAsText(file);
    }
  };

  const handleSummarize = (technique) => {
    if (!uploadedText) {
      alert("Primero sube un archivo de texto.");
      return;
    }

    setProcessing(true);
    // Simular llamada al backend para resumen
    setTimeout(() => {
      setSummary(`Resumen generado usando la técnica: ${technique}`);
      setProcessing(false);
    }, 2000); // Simulación de tiempo de procesamiento
  };

  return (
    <div className="bg-white min-h-screen flex flex-col relative">
      {/* Header */}
      <Header />

      {/* Sidebar */}
      <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />

      {/* Contenido principal */}
      <main className="flex-1 flex flex-col items-center gap-8 p-6">
        {/* Sección de carga de archivo */}
        <div className="bg-gray-100 p-6 rounded-lg shadow-lg w-full max-w-3xl">
          <h3 className="text-blue-800 font-bold text-center mb-4">Cargar archivo de texto</h3>
          <input
            type="file"
            accept=".txt"
            onChange={handleFileUpload}
            className="block w-full border border-gray-300 rounded-lg p-2"
          />
        </div>

        {/* Texto cargado */}
        <div className="bg-gray-100 p-6 rounded-lg shadow-lg w-full max-w-3xl">
          <h3 className="text-blue-800 font-bold text-center mb-4">Texto original</h3>
          <pre className="whitespace-pre-wrap text-sm text-gray-800">{uploadedText || "El texto cargado aparecerá aquí."}</pre>
        </div>

        {/* Botones para técnicas de resumen */}
        <div className="flex flex-wrap gap-4 justify-center">
          {["Técnica 1", "Técnica 2", "Técnica 3", "Técnica 4", "Técnica 5", "Técnica 6"].map((technique) => (
            <button
              key={technique}
              onClick={() => handleSummarize(technique)}
              className="py-2 px-4 rounded-md bg-blue-600 text-white font-semibold hover:bg-blue-700 transition"
              disabled={processing}
            >
              {processing ? "Procesando..." : technique}
            </button>
          ))}
        </div>

        {/* Resultado del resumen */}
        <div className="bg-gray-100 p-6 rounded-lg shadow-lg w-full max-w-3xl">
          <h3 className="text-blue-800 font-bold text-center mb-4">Texto resumido</h3>
          <pre className="whitespace-pre-wrap text-sm text-gray-800">{summary || "El resumen aparecerá aquí."}</pre>
        </div>
      </main>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default TextSummarizer;
